pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./MinerNFT.sol";

contract GachaSystem is Ownable {
    MinerNFT public minerNFT;
    IERC20 public mmToken;

    uint256 public constant GACHA_PRICE = 10 ether; // 10 MM
    uint256 public constant PACK_SIZE = 3; // 팩당 3개 NFT

    // 동일 확률 20%씩 (총 1000으로 normalize)
    uint256[5] public dropRates = [200, 200, 200, 200, 200];

    struct GachaPack {
        address buyer;
        uint256[3] tokenIds;
        MinerNFT.MinerType[3] minerTypes;
        uint256 timestamp;
    }

    mapping(address => GachaPack[]) public userPackHistory;
    uint256 public totalPacksSold;

    event GachaPackPurchased(
        address indexed buyer,
        uint256 indexed packId,
        uint256[3] tokenIds,
        MinerNFT.MinerType[3] minerTypes,
        uint256 price
    );

    event GachaConfigUpdated(uint256[5] newDropRates);

    constructor(address _minerNFT, address _mmToken) Ownable(msg.sender) {
        minerNFT = MinerNFT(_minerNFT);
        mmToken = IERC20(_mmToken);
    }

    function purchaseGachaPack()
        external
        returns (uint256[3] memory tokenIds, MinerNFT.MinerType[3] memory minerTypes)
    {
        require(mmToken.balanceOf(msg.sender) >= GACHA_PRICE, "Insufficient MM balance");
        require(mmToken.transferFrom(msg.sender, address(this), GACHA_PRICE), "MM transfer failed");

        // 3개 NFT 랜덤 생성
        for (uint256 i = 0; i < PACK_SIZE; i++) {
            minerTypes[i] = _rollMinerType(i);
        }

        // NFT 민팅
        MinerNFT.MinerType[] memory minerTypeArray = new MinerNFT.MinerType[](3);
        for (uint256 i = 0; i < 3; i++) {
            minerTypeArray[i] = minerTypes[i];
        }
        uint256[] memory mintedTokens = minerNFT.batchMintMiners(msg.sender, minerTypeArray);
        for (uint256 i = 0; i < 3; i++) {
            tokenIds[i] = mintedTokens[i];
        }

        // 팩 기록 저장
        GachaPack memory pack = GachaPack({
            buyer: msg.sender,
            tokenIds: tokenIds,
            minerTypes: minerTypes,
            timestamp: block.timestamp
        });

        userPackHistory[msg.sender].push(pack);
        totalPacksSold++;

        emit GachaPackPurchased(msg.sender, totalPacksSold, tokenIds, minerTypes, GACHA_PRICE);

        return (tokenIds, minerTypes);
    }

    function _rollMinerType(uint256 seed) private view returns (MinerNFT.MinerType) {
        uint256 randomValue = uint256(
            keccak256(abi.encodePacked(block.timestamp, block.prevrandao, msg.sender, seed, totalPacksSold))
        ) % 1000;

        uint256 cumulative = 0;
        for (uint256 i = 0; i < 5; i++) {
            cumulative += dropRates[i];
            if (randomValue < cumulative) {
                return MinerNFT.MinerType(i);
            }
        }

        // fallback (should never reach here)
        return MinerNFT.MinerType.BALANCED_SCAN;
    }

    function getUserPackHistory(address user) external view returns (GachaPack[] memory) {
        return userPackHistory[user];
    }

    function getUserPackCount(address user) external view returns (uint256) {
        return userPackHistory[user].length;
    }

    function getDropRates() external view returns (uint256[5] memory) {
        return dropRates;
    }

    function updateDropRates(uint256[5] calldata newDropRates) external onlyOwner {
        uint256 total = 0;
        for (uint256 i = 0; i < 5; i++) {
            total += newDropRates[i];
        }
        require(total == 1000, "Drop rates must sum to 1000");

        dropRates = newDropRates;
        emit GachaConfigUpdated(newDropRates);
    }

    function setMinerNFT(address _minerNFT) external onlyOwner {
        minerNFT = MinerNFT(_minerNFT);
    }

    function setMMToken(address _mmToken) external onlyOwner {
        mmToken = IERC20(_mmToken);
    }

    function withdrawMM(address to, uint256 amount) external onlyOwner {
        require(mmToken.transfer(to, amount), "MM transfer failed");
    }

    function withdrawAllMM(address to) external onlyOwner {
        uint256 balance = mmToken.balanceOf(address(this));
        require(mmToken.transfer(to, balance), "MM transfer failed");
    }

    function getContractStats()
        external
        view
        returns (uint256 totalPacks, uint256 totalRevenue, uint256 contractMMBalance)
    {
        return (totalPacksSold, totalPacksSold * GACHA_PRICE, mmToken.balanceOf(address(this)));
    }

    function simulateGachaPack(uint256 seed) external view returns (MinerNFT.MinerType[3] memory) {
        MinerNFT.MinerType[3] memory simulated;

        for (uint256 i = 0; i < PACK_SIZE; i++) {
            uint256 randomValue = uint256(keccak256(abi.encodePacked(seed, i, block.timestamp))) % 1000;

            uint256 cumulative = 0;
            for (uint256 j = 0; j < 5; j++) {
                cumulative += dropRates[j];
                if (randomValue < cumulative) {
                    simulated[i] = MinerNFT.MinerType(j);
                    break;
                }
            }
        }

        return simulated;
    }
}
","keccak256":"0x367073ba8f99bdc3253e178ca7b0d5ed18b58f40d5bb31f568a7399c77cbad3c","license":"MIT"},"contracts/GameManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract GameManager is Ownable {
    enum PatternType {
        EVEN, // 짝수
        ODD, // 홀수
        PRIME, // 소수
        PI, // π 관련 (314 포함/배수)
        SQUARE // 완전제곱수
    }

    struct Round {
        uint256 roundId;
        uint256 startTime;
        uint256 endTime;
        PatternType pattern;
        uint256 minRange;
        uint256 maxRange;
        bool isActive;
        uint256 rewardPool; // 라운드별 보상 풀 (MM 토큰)
        uint256 remainingRewards; // 남은 보상 개수
    }

    uint256 public currentRoundId;
    uint256 public constant ROUND_DURATION = 600; // 10분
    uint256 public constant REWARD_POOL_SIZE = 100 ether; // 라운드당 100 MM 토큰
    uint256 public constant BASIC_REWARD = 30 ether; // 성공당 30 MM 토큰

    IERC20 public mmToken;
    mapping(uint256 => Round) public rounds;

    event NewRoundStarted(
        uint256 indexed roundId,
        PatternType pattern,
        uint256 minRange,
        uint256 maxRange,
        uint256 startTime
    );

    event RoundEnded(uint256 indexed roundId, uint256 endTime, string reason);
    event RewardClaimed(uint256 indexed roundId, address indexed player, uint256 amount);
    event RewardPoolExhausted(uint256 indexed roundId);

    constructor(address _mmToken) Ownable(msg.sender) {
        mmToken = IERC20(_mmToken);
        _startNewRound();
    }

    function getCurrentRound() external view returns (Round memory) {
        return rounds[currentRoundId];
    }

    function isRoundActive() external view returns (bool) {
        Round memory round = rounds[currentRoundId];
        return round.isActive && block.timestamp < round.endTime;
    }

    function startNewRound() external onlyOwner {
        _startNewRound();
    }

    function _startNewRound() internal {
        // 이전 라운드 종료
        if (currentRoundId > 0) {
            rounds[currentRoundId].isActive = false;
            emit RoundEnded(currentRoundId, block.timestamp, "New round started");
        }

        currentRoundId++;

        // 새 라운드 패턴 및 범위 설정
        (PatternType pattern, uint256 minRange, uint256 maxRange) = _generateRoundParameters();

        rounds[currentRoundId] = Round({
            roundId: currentRoundId,
            startTime: block.timestamp,
            endTime: block.timestamp + ROUND_DURATION,
            pattern: pattern,
            minRange: minRange,
            maxRange: maxRange,
            isActive: true,
            rewardPool: REWARD_POOL_SIZE,
            remainingRewards: REWARD_POOL_SIZE / BASIC_REWARD
        });

        emit NewRoundStarted(currentRoundId, pattern, minRange, maxRange, block.timestamp);
    }

    function _generateRoundParameters() internal view returns (PatternType, uint256, uint256) {
        // 의사 랜덤으로 패턴과 범위 생성
        uint256 rand = uint256(keccak256(abi.encodePacked(block.timestamp, block.prevrandao, currentRoundId)));

        // 완전 랜덤한 패턴 선택
        PatternType pattern = PatternType(rand % 5);

        // 완전 랜덤한 범위 생성
        uint256 baseRange = 30000; // 최소 기준값
        uint256 maxRangeSpread = 50000; // 최대 범위 확산

        // 랜덤한 최소값 생성 (30,000 ~ 70,000)
        uint256 minRange = baseRange + (rand % (maxRangeSpread + 1));

        // 랜덤한 범위 크기 생성 (10,000 ~ 30,000)
        uint256 rangeSize = 10000 + ((rand >> 8) % 20001);
        uint256 maxRange = minRange + rangeSize;

        return (pattern, minRange, maxRange);
    }

    function autoAdvanceRound() external {
        require(
            block.timestamp >= rounds[currentRoundId].endTime || rounds[currentRoundId].remainingRewards == 0,
            "Round still active and rewards available"
        );

        string memory reason = rounds[currentRoundId].remainingRewards == 0 
            ? "Reward pool exhausted" 
            : "Time expired";

        rounds[currentRoundId].isActive = false;
        emit RoundEnded(currentRoundId, block.timestamp, reason);

        _startNewRound();
    }

    // 보상 지급 함수 (MiningEngine에서 호출)
    function claimReward(address player) external returns (bool) {
        Round storage round = rounds[currentRoundId];
        require(round.isActive, "Round not active");
        require(round.remainingRewards > 0, "No rewards remaining");

        // MM 토큰 전송
        require(mmToken.transfer(player, BASIC_REWARD), "Token transfer failed");

        round.remainingRewards--;
        round.rewardPool -= BASIC_REWARD;

        emit RewardClaimed(currentRoundId, player, BASIC_REWARD);

        // 보상풀이 소진되면 라운드 종료
        if (round.remainingRewards == 0) {
            emit RewardPoolExhausted(currentRoundId);
            round.isActive = false;
            emit RoundEnded(currentRoundId, block.timestamp, "Reward pool exhausted");
            _startNewRound();
        }

        return true;
    }

    function getRoundHistory(uint256 count) external view returns (Round[] memory) {
        uint256 startId = currentRoundId > count ? currentRoundId - count + 1 : 1;
        uint256 actualCount = currentRoundId - startId + 1;

        Round[] memory history = new Round[](actualCount);
        for (uint256 i = 0; i < actualCount; i++) {
            history[i] = rounds[startId + i];
        }

        return history;
    }
}
","keccak256":"0x3a899c315f3498445d0dedb467832817cc50de1db6bcba50488481c3d9f32437","license":"MIT"},"contracts/MinerNFT.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./GameManager.sol";

contract MinerNFT is ERC721, ERC721Enumerable, Ownable {
    enum MinerType {
        EVEN_BLASTER, // EvenBlaster
        PRIME_SNIPER, // PrimeSniper
        BALANCED_SCAN, // BalancedScan
        PI_SNIPER, // PiSniper
        SQUARE_SEEKER // SquareSeeker
    }

    struct MinerStats {
        MinerType minerType;
        uint256 minRange;
        uint256 maxRange;
        GameManager.PatternType specialization;
        uint256 baseSuccessRate; // 성공률 (10000 = 100%)
        string name;
    }

    uint256 private _nextTokenId = 1;
    mapping(uint256 => MinerStats) public minerStats;

    // 채굴기별 설정 (PRD 기준)
    mapping(MinerType => MinerStats) public minerConfig;

    // 민팅 권한 관리
    mapping(address => bool) public authorizedMinters;

    event MinerMinted(address indexed to, uint256 indexed tokenId, MinerType minerType, string name);
    event MinterAuthorized(address indexed minter, bool authorized);

    modifier onlyAuthorizedMinter() {
        require(authorizedMinters[msg.sender] || msg.sender == owner(), "Not authorized to mint");
        _;
    }

    constructor() ERC721("Monaminer NFT", "MINER") Ownable(msg.sender) {
        _initializeMinerConfigs();
    }

    function _initializeMinerConfigs() private {
        // EvenBlaster NFT
        minerConfig[MinerType.EVEN_BLASTER] = MinerStats({
            minerType: MinerType.EVEN_BLASTER,
            minRange: 40000,
            maxRange: 60000,
            specialization: GameManager.PatternType.EVEN,
            baseSuccessRate: 15, // 0.015%
            name: "EvenBlaster"
        });

        // PrimeSniper NFT
        minerConfig[MinerType.PRIME_SNIPER] = MinerStats({
            minerType: MinerType.PRIME_SNIPER,
            minRange: 74000,
            maxRange: 80000,
            specialization: GameManager.PatternType.PRIME,
            baseSuccessRate: 12, // 0.012%
            name: "PrimeSniper"
        });

        // BalancedScan NFT (범용)
        minerConfig[MinerType.BALANCED_SCAN] = MinerStats({
            minerType: MinerType.BALANCED_SCAN,
            minRange: 50000,
            maxRange: 70000,
            specialization: GameManager.PatternType.EVEN, // 모든 패턴에 대응
            baseSuccessRate: 8, // 0.008%
            name: "BalancedScan"
        });

        // PiSniper NFT
        minerConfig[MinerType.PI_SNIPER] = MinerStats({
            minerType: MinerType.PI_SNIPER,
            minRange: 60000,
            maxRange: 70000,
            specialization: GameManager.PatternType.PI,
            baseSuccessRate: 12, // 0.012%
            name: "PiSniper"
        });

        // SquareSeeker NFT
        minerConfig[MinerType.SQUARE_SEEKER] = MinerStats({
            minerType: MinerType.SQUARE_SEEKER,
            minRange: 30000,
            maxRange: 50000,
            specialization: GameManager.PatternType.SQUARE,
            baseSuccessRate: 14, // 0.014%
            name: "SquareSeeker"
        });
    }

    function mintMiner(address to, MinerType minerType) external onlyAuthorizedMinter returns (uint256) {
        uint256 tokenId = _nextTokenId++;

        MinerStats memory config = minerConfig[minerType];
        minerStats[tokenId] = config;

        _safeMint(to, tokenId);

        emit MinerMinted(to, tokenId, minerType, config.name);

        return tokenId;
    }

    function batchMintMiners(
        address to,
        MinerType[] calldata minerTypes
    ) external onlyAuthorizedMinter returns (uint256[] memory) {
        uint256[] memory tokenIds = new uint256[](minerTypes.length);

        for (uint256 i = 0; i < minerTypes.length; i++) {
            uint256 tokenId = _nextTokenId++;

            MinerStats memory config = minerConfig[minerTypes[i]];
            minerStats[tokenId] = config;

            _safeMint(to, tokenId);

            emit MinerMinted(to, tokenId, minerTypes[i], config.name);
            tokenIds[i] = tokenId;
        }

        return tokenIds;
    }

    // 민팅 권한 설정 함수
    function setMinter(address minter, bool authorized) external onlyOwner {
        authorizedMinters[minter] = authorized;
        emit MinterAuthorized(minter, authorized);
    }

    // 여러 minter를 한번에 설정
    function setMultipleMinters(address[] calldata minters, bool authorized) external onlyOwner {
        for (uint256 i = 0; i < minters.length; i++) {
            authorizedMinters[minters[i]] = authorized;
            emit MinterAuthorized(minters[i], authorized);
        }
    }

    function getMinerStats(uint256 tokenId) external view returns (MinerStats memory) {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");
        return minerStats[tokenId];
    }

    function getOwnedMiners(address owner) external view returns (uint256[] memory) {
        uint256 balance = balanceOf(owner);
        uint256[] memory tokens = new uint256[](balance);

        for (uint256 i = 0; i < balance; i++) {
            tokens[i] = tokenOfOwnerByIndex(owner, i);
        }

        return tokens;
    }

    function getMinerTypeStats(MinerType minerType) external view returns (MinerStats memory) {
        return minerConfig[minerType];
    }

    function calculateEffectiveSuccessRate(
        uint256 tokenId,
        GameManager.PatternType currentPattern
    ) external view returns (uint256) {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");

        MinerStats memory stats = minerStats[tokenId];

        // BalancedScan은 모든 패턴에 동일한 성공률
        if (stats.minerType == MinerType.BALANCED_SCAN) {
            return stats.baseSuccessRate;
        }

        // 특화 패턴과 일치하면 기본 성공률, 아니면 절반
        if (stats.specialization == currentPattern) {
            return stats.baseSuccessRate;
        } else {
            return stats.baseSuccessRate / 2;
        }
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");

        MinerStats memory stats = minerStats[tokenId];

        return
            string(
                abi.encodePacked(
                    "https://api.monaminer.com/nft/",
                    Strings.toString(uint256(stats.minerType)),
                    "/",
                    Strings.toString(tokenId)
                )
            );
    }

    // Override required functions
    function _update(
        address to,
        uint256 tokenId,
        address auth
    ) internal override(ERC721, ERC721Enumerable) returns (address) {
        return super._update(to, tokenId, auth);
    }

    function _increaseBalance(address account, uint128 value) internal override(ERC721, ERC721Enumerable) {
        super._increaseBalance(account, value);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721Enumerable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}
