interface IEntropyV2 is EntropyEventsV2 {
    /// @notice Request a random number using the default provider with default gas limit
    /// @return assignedSequenceNumber A unique identifier for this request
    /// @dev The address calling this function should be a contract that inherits from the IEntropyConsumer interface.
    /// The `entropyCallback` method on that interface will receive a callback with the returned sequence number and
    /// the generated random number.
    ///
    /// `entropyCallback` will be run with the `gasLimit` provided to this function.
    /// The `gasLimit` will be rounded up to a multiple of 10k (e.g., 19000 -> 20000), and furthermore is lower bounded
    /// by the provider's configured default limit.
    ///
    /// This method will revert unless the caller provides a sufficient fee (at least `getFeeV2()`) as msg.value.
    /// Note that the fee can change over time. Callers of this method should explicitly compute `getFeeV2()`
    /// prior to each invocation (as opposed to hardcoding a value). Further note that excess value is *not* refunded to the caller.
    ///
    /// Note that this method uses an in-contract PRNG to generate the user's contribution to the random number.
    /// This approach modifies the security guarantees such that a dishonest validator and provider can
    /// collude to manipulate the result (as opposed to a malicious user and provider). That is, the user
    /// now trusts the validator honestly draw a random number. If you wish to avoid this trust assumption,
    /// call a variant of `requestV2` that accepts a `userRandomNumber` parameter.
    function requestV2()
        external
        payable
        returns (uint64 assignedSequenceNumber);

    /// @notice Request a random number using the default provider with specified gas limit
    /// @param gasLimit The gas limit for the callback function.
    /// @return assignedSequenceNumber A unique identifier for this request
    /// @dev The address calling this function should be a contract that inherits from the IEntropyConsumer interface.
    /// The `entropyCallback` method on that interface will receive a callback with the returned sequence number and
    /// the generated random number.
    ///
    /// `entropyCallback` will be run with the `gasLimit` provided to this function.
    /// The `gasLimit` will be rounded up to a multiple of 10k (e.g., 19000 -> 20000), and furthermore is lower bounded
    /// by the provider's configured default limit.
    ///
    /// This method will revert unless the caller provides a sufficient fee (at least `getFeeV2(gasLimit)`) as msg.value.
    /// Note that the fee can change over time. Callers of this method should explicitly compute `getFeeV2(gasLimit)`
    /// prior to each invocation (as opposed to hardcoding a value). Further note that excess value is *not* refunded to the caller.
    ///
    /// Note that this method uses an in-contract PRNG to generate the user's contribution to the random number.
    /// This approach modifies the security guarantees such that a dishonest validator and provider can
    /// collude to manipulate the result (as opposed to a malicious user and provider). That is, the user
    /// now trusts the validator honestly draw a random number. If you wish to avoid this trust assumption,
    /// call a variant of `requestV2` that accepts a `userRandomNumber` parameter.
    function requestV2(
        uint32 gasLimit
    ) external payable returns (uint64 assignedSequenceNumber);

    /// @notice Request a random number from a specific provider with specified gas limit
    /// @param provider The address of the provider to request from
    /// @param gasLimit The gas limit for the callback function
    /// @return assignedSequenceNumber A unique identifier for this request
    /// @dev The address calling this function should be a contract that inherits from the IEntropyConsumer interface.
    /// The `entropyCallback` method on that interface will receive a callback with the returned sequence number and
    /// the generated random number.
    ///
    /// `entropyCallback` will be run with the `gasLimit` provided to this function.
    /// The `gasLimit` will be rounded up to a multiple of 10k (e.g., 19000 -> 20000), and furthermore is lower bounded
    /// by the provider's configured default limit.
    ///
    /// This method will revert unless the caller provides a sufficient fee (at least `getFeeV2(provider, gasLimit)`) as msg.value.
    /// Note that provider fees can change over time. Callers of this method should explicitly compute `getFeeV2(provider, gasLimit)`
    /// prior to each invocation (as opposed to hardcoding a value). Further note that excess value is *not* refunded to the caller.
    ///
    /// Note that this method uses an in-contract PRNG to generate the user's contribution to the random number.
    /// This approach modifies the security guarantees such that a dishonest validator and provider can
    /// collude to manipulate the result (as opposed to a malicious user and provider). That is, the user
    /// now trusts the validator honestly draw a random number. If you wish to avoid this trust assumption,
    /// call a variant of `requestV2` that accepts a `userRandomNumber` parameter.
    function requestV2(
        address provider,
        uint32 gasLimit
    ) external payable returns (uint64 assignedSequenceNumber);

    /// @notice Request a random number from a specific provider with a user-provided random number and gas limit
    /// @param provider The address of the provider to request from
    /// @param userRandomNumber A random number provided by the user for additional entropy
    /// @param gasLimit The gas limit for the callback function. Pass 0 to get a sane default value -- see note below.
    /// @return assignedSequenceNumber A unique identifier for this request
    /// @dev The address calling this function should be a contract that inherits from the IEntropyConsumer interface.
    /// The `entropyCallback` method on that interface will receive a callback with the returned sequence number and
    /// the generated random number.
    ///
    /// `entropyCallback` will be run with the `gasLimit` provided to this function.
    /// The `gasLimit` will be rounded up to a multiple of 10k (e.g., 19000 -> 20000), and furthermore is lower bounded
    /// by the provider's configured default limit.
    ///
    /// This method will revert unless the caller provides a sufficient fee (at least `getFeeV2(provider, gasLimit)`) as msg.value.
    /// Note that provider fees can change over time. Callers of this method should explicitly compute `getFeeV2(provider, gasLimit)`
    /// prior to each invocation (as opposed to hardcoding a value). Further note that excess value is *not* refunded to the caller.
    function requestV2(
        address provider,
        bytes32 userRandomNumber,
        uint32 gasLimit
    ) external payable returns (uint64 assignedSequenceNumber);

    /// @notice Get information about a specific entropy provider
    /// @param provider The address of the provider to query
    /// @return info The provider information including configuration, fees, and operational status
    /// @dev This method returns detailed information about a provider's configuration and capabilities.
    /// The returned ProviderInfo struct contains information such as the provider's fee structure and gas limits.
    function getProviderInfoV2(
        address provider
    ) external view returns (EntropyStructsV2.ProviderInfo memory info);

    /// @notice Get the address of the default entropy provider
    /// @return provider The address of the default provider
    /// @dev This method returns the address of the provider that will be used when no specific provider is specified
    /// in the requestV2 calls. The default provider can be used to get the base fee and gas limit information.
    function getDefaultProvider() external view returns (address provider);

    /// @notice Get information about a specific request
    /// @param provider The address of the provider that handled the request
    /// @param sequenceNumber The unique identifier of the request
    /// @return req The request information including status, random number, and other metadata
    /// @dev This method allows querying the state of a previously made request. The returned Request struct
    /// contains information about whether the request was fulfilled, the generated random number (if available),
    /// and other metadata about the request.
    function getRequestV2(
        address provider,
        uint64 sequenceNumber
    ) external view returns (EntropyStructsV2.Request memory req);

    /// @notice Get the fee charged by the default provider for the default gas limit
    /// @return feeAmount The fee amount in wei
    /// @dev This method returns the base fee required to make a request using the default provider with
    /// the default gas limit. This fee should be passed as msg.value when calling requestV2().
    /// The fee can change over time, so this method should be called before each request.
    function getFeeV2() external view returns (uint128 feeAmount);

    /// @notice Get the fee charged by the default provider for a specific gas limit
    /// @param gasLimit The gas limit for the callback function
    /// @return feeAmount The fee amount in wei
    /// @dev This method returns the fee required to make a request using the default provider with
    /// the specified gas limit. This fee should be passed as msg.value when calling requestV2(gasLimit).
    /// The fee can change over time, so this method should be called before each request.
    function getFeeV2(
        uint32 gasLimit
    ) external view returns (uint128 feeAmount);

    /// @notice Get the fee charged by a specific provider for a request with a given gas limit
    /// @param provider The address of the provider to query
    /// @param gasLimit The gas limit for the callback function
    /// @return feeAmount The fee amount in wei
    /// @dev This method returns the fee required to make a request using the specified provider with
    /// the given gas limit. This fee should be passed as msg.value when calling requestV2(provider, gasLimit)
    /// or requestV2(provider, userRandomNumber, gasLimit). The fee can change over time, so this method
    /// should be called before each request.
    function getFeeV2(
        address provider,
        uint32 gasLimit
    ) external view returns (uint128 feeAmount);
}

contracts/GachaSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./MinerNFT.sol";

contract GachaSystem is Ownable {
    MinerNFT public minerNFT;
    IERC20 public monToken;

    uint256 public constant GACHA_PRICE = 10 ether; // 10 MON
    uint256 public constant PACK_SIZE = 3; // 팩당 3개 NFT

    // 동일 확률 20%씩 (총 1000으로 normalize)
    uint256[5] public dropRates = [200, 200, 200, 200, 200];

    struct GachaPack {
        address buyer;
        uint256[3] tokenIds;
        MinerNFT.MinerType[3] minerTypes;
        uint256 timestamp;
    }

    mapping(address => GachaPack[]) public userPackHistory;
    uint256 public totalPacksSold;

    event GachaPackPurchased(
        address indexed buyer,
        uint256 indexed packId,
        uint256[3] tokenIds,
        MinerNFT.MinerType[3] minerTypes,
        uint256 price
    );

    event GachaConfigUpdated(uint256[5] newDropRates);

    constructor(address _minerNFT, address _monToken) Ownable(msg.sender) {
        minerNFT = MinerNFT(_minerNFT);
        monToken = IERC20(_monToken);
    }

    function purchaseGachaPack()
        external
        returns (uint256[3] memory tokenIds, MinerNFT.MinerType[3] memory minerTypes)
    {
        require(monToken.balanceOf(msg.sender) >= GACHA_PRICE, "Insufficient MON balance");
        require(monToken.transferFrom(msg.sender, address(this), GACHA_PRICE), "MON transfer failed");

        // 3개 NFT 랜덤 생성
        for (uint256 i = 0; i < PACK_SIZE; i++) {
            minerTypes[i] = _rollMinerType(i);
        }

        // NFT 민팅
        MinerNFT.MinerType[] memory minerTypeArray = new MinerNFT.MinerType[](3);
        for (uint256 i = 0; i < 3; i++) {
            minerTypeArray[i] = minerTypes[i];
        }
        uint256[] memory mintedTokens = minerNFT.batchMintMiners(msg.sender, minerTypeArray);
        for (uint256 i = 0; i < 3; i++) {
            tokenIds[i] = mintedTokens[i];
        }

        // 팩 기록 저장
        GachaPack memory pack = GachaPack({
            buyer: msg.sender,
            tokenIds: tokenIds,
            minerTypes: minerTypes,
            timestamp: block.timestamp
        });

        userPackHistory[msg.sender].push(pack);
        totalPacksSold++;

        emit GachaPackPurchased(msg.sender, totalPacksSold, tokenIds, minerTypes, GACHA_PRICE);

        return (tokenIds, minerTypes);
    }

    function _rollMinerType(uint256 seed) private view returns (MinerNFT.MinerType) {
        uint256 randomValue = uint256(
            keccak256(abi.encodePacked(block.timestamp, block.prevrandao, msg.sender, seed, totalPacksSold))
        ) % 1000;

        uint256 cumulative = 0;
        for (uint256 i = 0; i < 5; i++) {
            cumulative += dropRates[i];
            if (randomValue < cumulative) {
                return MinerNFT.MinerType(i);
            }
        }

        // fallback (should never reach here)
        return MinerNFT.MinerType.BALANCED_SCAN;
    }

    function getUserPackHistory(address user) external view returns (GachaPack[] memory) {
        return userPackHistory[user];
    }

    function getUserPackCount(address user) external view returns (uint256) {
        return userPackHistory[user].length;
    }

    function getDropRates() external view returns (uint256[5] memory) {
        return dropRates;
    }

    function updateDropRates(uint256[5] calldata newDropRates) external onlyOwner {
        uint256 total = 0;
        for (uint256 i = 0; i < 5; i++) {
            total += newDropRates[i];
        }
        require(total == 1000, "Drop rates must sum to 1000");

        dropRates = newDropRates;
        emit GachaConfigUpdated(newDropRates);
    }

    function setMinerNFT(address _minerNFT) external onlyOwner {
        minerNFT = MinerNFT(_minerNFT);
    }

    function setMonToken(address _monToken) external onlyOwner {
        monToken = IERC20(_monToken);
    }

    function withdrawMON(address to, uint256 amount) external onlyOwner {
        require(monToken.transfer(to, amount), "MON transfer failed");
    }

    function withdrawAllMON(address to) external onlyOwner {
        uint256 balance = monToken.balanceOf(address(this));
        require(monToken.transfer(to, balance), "MON transfer failed");
    }

    function getContractStats()
        external
        view
        returns (uint256 totalPacks, uint256 totalRevenue, uint256 contractMONBalance)
    {
        return (totalPacksSold, totalPacksSold * GACHA_PRICE, monToken.balanceOf(address(this)));
    }

    function simulateGachaPack(uint256 seed) external view returns (MinerNFT.MinerType[3] memory) {
        MinerNFT.MinerType[3] memory simulated;

        for (uint256 i = 0; i < PACK_SIZE; i++) {
            uint256 randomValue = uint256(keccak256(abi.encodePacked(seed, i, block.timestamp))) % 1000;

            uint256 cumulative = 0;
            for (uint256 j = 0; j < 5; j++) {
                cumulative += dropRates[j];
                if (randomValue < cumulative) {
                    simulated[i] = MinerNFT.MinerType(j);
                    break;
                }
            }
        }

        return simulated;
    }
}

contracts/GameManagerSimple.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";

contract GameManagerSimple is Ownable {
    enum PatternType {
        EVEN, // 짝수
        ODD, // 홀수
        PRIME, // 소수
        PI, // π 관련 (314 포함/배수)
        SQUARE // 완전제곱수
    }

    struct Round {
        uint256 roundId;
        uint256 startTime;
        uint256 endTime;
        PatternType pattern;
        uint256 minRange;
        uint256 maxRange;
        bool isActive;
        uint256[] answerPool; // 정답 난수 풀
        bool answerPoolGenerated; // 정답 풀 생성 완료 여부
    }

    uint256 public currentRoundId;
    uint256 public constant ROUND_DURATION = 600; // 10분
    uint256 public constant ANSWER_POOL_SIZE = 100; // 정답 풀 크기

    mapping(uint256 => Round) public rounds;

    event NewRoundStarted(
        uint256 indexed roundId,
        PatternType pattern,
        uint256 minRange,
        uint256 maxRange,
        uint256 startTime
    );

    event RoundEnded(uint256 indexed roundId, uint256 endTime);

    event AnswerPoolGenerated(uint256 indexed roundId, uint256 poolSize);

    constructor() Ownable(msg.sender) {
        _startNewRound();
    }

    function _startNewRound() internal {
        // 이전 라운드 종료
        if (currentRoundId > 0) {
            rounds[currentRoundId].isActive = false;
            rounds[currentRoundId].endTime = block.timestamp;
            emit RoundEnded(currentRoundId, block.timestamp);
        }

        currentRoundId++;

        // 새 라운드 설정 생성
        (PatternType pattern, uint256 minRange, uint256 maxRange) = _generateRoundSettings();

        rounds[currentRoundId] = Round({
            roundId: currentRoundId,
            startTime: block.timestamp,
            endTime: block.timestamp + ROUND_DURATION,
            pattern: pattern,
            minRange: minRange,
            maxRange: maxRange,
            isActive: true,
            answerPool: new uint256[](0),
            answerPoolGenerated: false
        });

        emit NewRoundStarted(currentRoundId, pattern, minRange, maxRange, block.timestamp);

        // 정답 풀 즉시 생성 (Simple 버전)
        _generateAnswerPoolSimple(currentRoundId);
    }

    function _generateRoundSettings() internal view returns (PatternType, uint256, uint256) {
        // 유사 랜덤값 생성 (블록 데이터 기반)
        uint256 randomValue = uint256(
            keccak256(abi.encodePacked(block.timestamp, blockhash(block.number - 1), currentRoundId))
        );

        // 패턴 선택 (5가지 중 하나)
        PatternType pattern = PatternType(randomValue % 5);

        // 범위 설정
        uint256 baseMin = 30000 + (randomValue % 20000); // 30000-50000
        uint256 baseMax = 60000 + (randomValue % 20000); // 60000-80000

        // 패턴별 조정
        uint256 minRange = baseMin + ((randomValue / 100) % 10000);
        uint256 maxRange = baseMax + ((randomValue / 1000) % 10000);

        return (pattern, minRange, maxRange);
    }

    function _generateAnswerPoolSimple(uint256 roundId) internal {
        Round storage round = rounds[roundId];

        // 의사 랜덤 시드
        uint256 seed = uint256(
            keccak256(abi.encodePacked(block.timestamp, block.prevrandao, roundId, msg.sender))
        );

        uint256 generated = 0;
        uint256 attempts = 0;
        uint256 maxAttempts = (round.maxRange - round.minRange + 1) * 2; // 충분한 시도 횟수

        while (generated < ANSWER_POOL_SIZE && attempts < maxAttempts) {
            uint256 randomNum = uint256(
                keccak256(abi.encodePacked(seed, attempts, generated))
            );

            uint256 candidate = round.minRange + (randomNum % (round.maxRange - round.minRange + 1));

            if (_checkPattern(candidate, round.pattern)) {
                round.answerPool.push(candidate);
                generated++;
            }

            attempts++;
        }

        round.answerPoolGenerated = true;
        emit AnswerPoolGenerated(roundId, round.answerPool.length);
    }

    function _checkPattern(uint256 number, PatternType pattern) internal pure returns (bool) {
        if (pattern == PatternType.EVEN) {
            return number % 2 == 0;
        } else if (pattern == PatternType.ODD) {
            return number % 2 == 1;
        } else if (pattern == PatternType.PRIME) {
            return _isPrime(number);
        } else if (pattern == PatternType.PI) {
            return _isPiRelated(number);
        } else if (pattern == PatternType.SQUARE) {
            return _isPerfectSquare(number);
        }
        return false;
    }

    function _isPrime(uint256 n) internal pure returns (bool) {
        if (n < 2) return false;
        if (n == 2) return true;
        if (n % 2 == 0) return false;

        for (uint256 i = 3; i * i <= n; i += 2) {
            if (n % i == 0) return false;
        }
        return true;
    }

    function _isPiRelated(uint256 n) internal pure returns (bool) {
        // 314를 포함하거나 314의 배수
        string memory numStr = _toString(n);
        return _contains(numStr, "314") || n % 314 == 0;
    }

    function _isPerfectSquare(uint256 n) internal pure returns (bool) {
        if (n == 0) return true;
        uint256 sqrt = _sqrt(n);
        return sqrt * sqrt == n;
    }

    function _sqrt(uint256 x) internal pure returns (uint256) {
        if (x == 0) return 0;
        uint256 result = 1;
        if (x >> 128 > 0) {
            x >>= 128;
            result <<= 64;
        }
        if (x >> 64 > 0) {
            x >>= 64;
            result <<= 32;
        }
        if (x >> 32 > 0) {
            x >>= 32;
            result <<= 16;
        }
        if (x >> 16 > 0) {
            x >>= 16;
            result <<= 8;
        }
        if (x >> 8 > 0) {
            x >>= 8;
            result <<= 4;
        }
        if (x >> 4 > 0) {
            x >>= 4;
            result <<= 2;
        }
        if (x >> 2 > 0) {
            result <<= 1;
        }
        return result;
    }

    function _toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) return "0";
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }

    function _contains(string memory str, string memory substr) internal pure returns (bool) {
        bytes memory strBytes = bytes(str);
        bytes memory substrBytes = bytes(substr);

        if (substrBytes.length > strBytes.length) return false;

        for (uint256 i = 0; i <= strBytes.length - substrBytes.length; i++) {
            bool found = true;
            for (uint256 j = 0; j < substrBytes.length; j++) {
                if (strBytes[i + j] != substrBytes[j]) {
                    found = false;
                    break;
                }
            }
            if (found) return true;
        }
        return false;
    }

    // Public functions
    function getCurrentRound() external view returns (Round memory) {
        return rounds[currentRoundId];
    }

    function getRound(uint256 roundId) external view returns (Round memory) {
        return rounds[roundId];
    }

    function isRoundActive() external view returns (bool) {
        return rounds[currentRoundId].isActive && block.timestamp < rounds[currentRoundId].endTime;
    }

    function getRoundTimeRemaining() external view returns (uint256) {
        if (!rounds[currentRoundId].isActive) return 0;
        if (block.timestamp >= rounds[currentRoundId].endTime) return 0;
        return rounds[currentRoundId].endTime - block.timestamp;
    }

    function getAnswerPool(uint256 roundId) external view returns (uint256[] memory) {
        return rounds[roundId].answerPool;
    }

    function isAnswerPoolGenerated(uint256 roundId) external view returns (bool) {
        return rounds[roundId].answerPoolGenerated;
    }

    function checkRoundExpiry() external {
        if (rounds[currentRoundId].isActive && block.timestamp >= rounds[currentRoundId].endTime) {
            _startNewRound();
        }
    }

    // Admin functions
    function forceNewRound() external onlyOwner {
        _startNewRound();
    }

    function setRoundDuration(uint256 _duration) external onlyOwner {
        // Note: This would need to be implemented if we want dynamic durations
        // For now, ROUND_DURATION is constant
    }
}

contracts/MinerNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./GameManagerSimple.sol";

contract MinerNFT is ERC721, ERC721Enumerable, Ownable {
    enum MinerType {
        EVEN_BLASTER, // EvenBlaster
        PRIME_SNIPER, // PrimeSniper
        BALANCED_SCAN, // BalancedScan
        PI_SNIPER, // PiSniper
        SQUARE_SEEKER // SquareSeeker
    }

    struct MinerStats {
        MinerType minerType;
        uint256 minRange;
        uint256 maxRange;
        GameManagerSimple.PatternType specialization;
        uint256 baseSuccessRate; // 성공률 (10000 = 100%)
        string name;
    }

    uint256 private _nextTokenId = 1;
    mapping(uint256 => MinerStats) public minerStats;

    // 채굴기별 설정 (PRD 기준)
    mapping(MinerType => MinerStats) public minerConfig;

    event MinerMinted(address indexed to, uint256 indexed tokenId, MinerType minerType, string name);

    constructor() ERC721("Monaminer NFT", "MINER") Ownable(msg.sender) {
        _initializeMinerConfigs();
    }

    function _initializeMinerConfigs() private {
        // EvenBlaster NFT
        minerConfig[MinerType.EVEN_BLASTER] = MinerStats({
            minerType: MinerType.EVEN_BLASTER,
            minRange: 40000,
            maxRange: 60000,
            specialization: GameManagerSimple.PatternType.EVEN,
            baseSuccessRate: 15, // 0.015%
            name: "EvenBlaster"
        });

        // PrimeSniper NFT
        minerConfig[MinerType.PRIME_SNIPER] = MinerStats({
            minerType: MinerType.PRIME_SNIPER,
            minRange: 74000,
            maxRange: 80000,
            specialization: GameManagerSimple.PatternType.PRIME,
            baseSuccessRate: 12, // 0.012%
            name: "PrimeSniper"
        });

        // BalancedScan NFT (범용)
        minerConfig[MinerType.BALANCED_SCAN] = MinerStats({
            minerType: MinerType.BALANCED_SCAN,
            minRange: 50000,
            maxRange: 70000,
            specialization: GameManagerSimple.PatternType.EVEN, // 모든 패턴에 대응
            baseSuccessRate: 8, // 0.008%
            name: "BalancedScan"
        });

        // PiSniper NFT
        minerConfig[MinerType.PI_SNIPER] = MinerStats({
            minerType: MinerType.PI_SNIPER,
            minRange: 60000,
            maxRange: 70000,
            specialization: GameManagerSimple.PatternType.PI,
            baseSuccessRate: 12, // 0.012%
            name: "PiSniper"
        });

        // SquareSeeker NFT
        minerConfig[MinerType.SQUARE_SEEKER] = MinerStats({
            minerType: MinerType.SQUARE_SEEKER,
            minRange: 30000,
            maxRange: 50000,
            specialization: GameManagerSimple.PatternType.SQUARE,
            baseSuccessRate: 14, // 0.014%
            name: "SquareSeeker"
        });
    }

    function mintMiner(address to, MinerType minerType) external onlyOwner returns (uint256) {
        uint256 tokenId = _nextTokenId++;

        MinerStats memory config = minerConfig[minerType];
        minerStats[tokenId] = config;

        _safeMint(to, tokenId);

        emit MinerMinted(to, tokenId, minerType, config.name);

        return tokenId;
    }

    function batchMintMiners(
        address to,
        MinerType[] calldata minerTypes
    ) external onlyOwner returns (uint256[] memory) {
        uint256[] memory tokenIds = new uint256[](minerTypes.length);

        for (uint256 i = 0; i < minerTypes.length; i++) {
            uint256 tokenId = _nextTokenId++;

            MinerStats memory config = minerConfig[minerTypes[i]];
            minerStats[tokenId] = config;

            _safeMint(to, tokenId);

            emit MinerMinted(to, tokenId, minerTypes[i], config.name);
            tokenIds[i] = tokenId;
        }

        return tokenIds;
    }

    function getMinerStats(uint256 tokenId) external view returns (MinerStats memory) {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");
        return minerStats[tokenId];
    }

    function getOwnedMiners(address owner) external view returns (uint256[] memory) {
        uint256 balance = balanceOf(owner);
        uint256[] memory tokens = new uint256[](balance);

        for (uint256 i = 0; i < balance; i++) {
            tokens[i] = tokenOfOwnerByIndex(owner, i);
        }

        return tokens;
    }

    function getMinerTypeStats(MinerType minerType) external view returns (MinerStats memory) {
        return minerConfig[minerType];
    }

    function calculateEffectiveSuccessRate(
        uint256 tokenId,
        GameManagerSimple.PatternType currentPattern
    ) external view returns (uint256) {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");

        MinerStats memory stats = minerStats[tokenId];

        // BalancedScan은 모든 패턴에 동일한 성공률
        if (stats.minerType == MinerType.BALANCED_SCAN) {
            return stats.baseSuccessRate;
        }

        // 특화 패턴과 일치하면 기본 성공률, 아니면 절반
        if (stats.specialization == currentPattern) {
            return stats.baseSuccessRate;
        } else {
            return stats.baseSuccessRate / 2;
        }
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");

        MinerStats memory stats = minerStats[tokenId];

        return
            string(
                abi.encodePacked(
                    "https://api.monaminer.com/nft/",
                    Strings.toString(uint256(stats.minerType)),
                    "/",
                    Strings.toString(tokenId)
                )
            );
    }

    // Override required functions
    function _update(
        address to,
        uint256 tokenId,
        address auth
    ) internal override(ERC721, ERC721Enumerable) returns (address) {
        return super._update(to, tokenId, auth);
    }

    function _increaseBalance(address account, uint128 value) internal override(ERC721, ERC721Enumerable) {
        super._increaseBalance(account, value);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721Enumerable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

contracts/MiningEngine.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./GameManagerSimple.sol";
import "./MinerNFT.sol";
import "./RewardManager.sol";
import "@pythnetwork/entropy-sdk-solidity/IEntropy.sol";
import "@pythnetwork/entropy-sdk-solidity/IEntropyConsumer.sol";

contract MiningEngine is Ownable, ReentrancyGuard, IEntropyConsumer {
    GameManagerSimple public gameManager;
    MinerNFT public minerNFT;
    RewardManager public rewardManager;
    IEntropy public entropy;
    address public entropyProvider;

    struct MiningSession {
        address player;
        uint256[3] nftIds;
        uint256 roundId;
        uint256 startTime;
        bool isActive;
        uint256 totalAttempts;
        uint256 totalSuccesses;
        uint256 lastMiningTime;
    }

    struct MiningAttempt {
        address player;
        uint256 nftId;
        uint256 roundId;
        uint256 randomNumber;
        bool success;
        uint256 timestamp;
    }

    struct PendingRequest {
        address player;
        uint256 nftId;
        uint256 roundId;
        uint256 batchIndex;
        uint256 timestamp;
    }

    uint256 public constant TPS_PER_MINER = 50; // 초당 50 트랜잭션
    uint256 public constant MAX_MINING_DURATION = 600; // 10분
    uint256 public constant MINING_INTERVAL = 200; // 0.2초 (200ms)
    uint256 public constant BATCH_SIZE = 10; // 배치당 10개 트랜잭션

    mapping(address => MiningSession) public activeSessions;
    mapping(address => MiningAttempt[]) public miningHistory;
    mapping(address => uint256) public playerTotalSuccesses;
    mapping(uint64 => PendingRequest) public pendingRequests;

    event MiningSessionStarted(address indexed player, uint256[3] nftIds, uint256 roundId, uint256 startTime);

    event MiningSuccess(
        address indexed player,
        uint256 indexed nftId,
        uint256 randomNumber,
        uint256 roundId,
        uint256 timestamp
    );

    event MiningSessionEnded(address indexed player, uint256 totalAttempts, uint256 totalSuccesses, uint256 endTime);

    event BatchMiningCompleted(address indexed player, uint256 batchAttempts, uint256 batchSuccesses);

    constructor(
        address _gameManager,
        address _minerNFT,
        address _rewardManager,
        address _entropy,
        address _entropyProvider
    ) Ownable(msg.sender) {
        gameManager = GameManagerSimple(_gameManager);
        minerNFT = MinerNFT(_minerNFT);
        rewardManager = RewardManager(_rewardManager);
        entropy = IEntropy(_entropy);
        entropyProvider = _entropyProvider;
    }

    function startMining(uint256[3] calldata nftIds) external nonReentrant {
        require(!activeSessions[msg.sender].isActive, "Already mining");
        require(gameManager.isRoundActive(), "No active round");

        // NFT 소유권 확인
        for (uint256 i = 0; i < 3; i++) {
            require(minerNFT.ownerOf(nftIds[i]) == msg.sender, "Not NFT owner");
        }

        GameManagerSimple.Round memory currentRound = gameManager.getCurrentRound();

        activeSessions[msg.sender] = MiningSession({
            player: msg.sender,
            nftIds: nftIds,
            roundId: currentRound.roundId,
            startTime: block.timestamp,
            isActive: true,
            totalAttempts: 0,
            totalSuccesses: 0,
            lastMiningTime: block.timestamp
        });

        emit MiningSessionStarted(msg.sender, nftIds, currentRound.roundId, block.timestamp);
    }

    function performBatchMining() external nonReentrant {
        MiningSession storage session = activeSessions[msg.sender];
        require(session.isActive, "No active session");
        require(block.timestamp >= session.lastMiningTime + (MINING_INTERVAL / 1000), "Mining too fast");
        require(block.timestamp < session.startTime + MAX_MINING_DURATION, "Mining session expired");

        GameManagerSimple.Round memory currentRound = gameManager.getCurrentRound();
        require(session.roundId == currentRound.roundId, "Round changed");

        uint256 batchSuccesses = 0;

        // 3개 NFT × 10개씩 = 30개 배치 처리 (Pyth Entropy 사용)
        for (uint256 i = 0; i < 3; i++) {
            for (uint256 j = 0; j < BATCH_SIZE; j++) {
                uint64 sequenceNumber = _requestRandomness(session.nftIds[i], j);

                // 대기 중인 요청 저장
                pendingRequests[sequenceNumber] = PendingRequest({
                    player: msg.sender,
                    nftId: session.nftIds[i],
                    roundId: session.roundId,
                    batchIndex: session.totalAttempts,
                    timestamp: block.timestamp
                });

                session.totalAttempts++;
            }
        }

        session.lastMiningTime = block.timestamp;

        emit BatchMiningCompleted(msg.sender, BATCH_SIZE * 3, batchSuccesses);

        // 세션 만료 체크
        if (block.timestamp >= session.startTime + MAX_MINING_DURATION) {
            _endMiningSession(msg.sender);
        }
    }

    function stopMining() external {
        require(activeSessions[msg.sender].isActive, "No active session");
        _endMiningSession(msg.sender);
    }

    function _endMiningSession(address player) internal {
        MiningSession storage session = activeSessions[player];

        // 완주 보너스 체크 (90,000 트랜잭션)
        if (session.totalAttempts >= 90000) {
            rewardManager.distributeCompletionBonus(player);
        }

        emit MiningSessionEnded(player, session.totalAttempts, session.totalSuccesses, block.timestamp);

        session.isActive = false;
    }

    function _requestRandomness(uint256 nftId, uint256 seed) internal returns (uint64) {
        // 사용자 랜덤값 생성
        bytes32 userRandomness = keccak256(
            abi.encodePacked(block.timestamp, msg.sender, nftId, seed, activeSessions[msg.sender].totalAttempts)
        );

        // Pyth Entropy에 랜덤값 요청
        uint256 fee = entropy.getFee(entropyProvider);
        require(address(this).balance >= fee, "Insufficient ETH for entropy fee");

        return entropy.requestWithCallback{ value: fee }(entropyProvider, userRandomness);
    }

    function _checkMiningSuccess(
        uint256 randomNumber,
        uint256 nftId,
        GameManagerSimple.Round memory round
    ) internal view returns (bool) {
        // 범위 체크
        uint256 targetNumber = (randomNumber % (round.maxRange - round.minRange + 1)) + round.minRange;

        // 패턴 체크
        bool patternMatch = _checkPattern(targetNumber, round.pattern);
        if (!patternMatch) {
            return false;
        }

        // 성공률 체크
        uint256 effectiveSuccessRate = minerNFT.calculateEffectiveSuccessRate(nftId, round.pattern);
        uint256 successThreshold = (randomNumber % 10000);

        return successThreshold < effectiveSuccessRate;
    }

    function _checkPattern(uint256 number, GameManagerSimple.PatternType pattern) internal pure returns (bool) {
        if (pattern == GameManagerSimple.PatternType.EVEN) {
            return number % 2 == 0;
        } else if (pattern == GameManagerSimple.PatternType.ODD) {
            return number % 2 == 1;
        } else if (pattern == GameManagerSimple.PatternType.PRIME) {
            return _isPrime(number);
        } else if (pattern == GameManagerSimple.PatternType.PI) {
            return _isPiRelated(number);
        } else if (pattern == GameManagerSimple.PatternType.SQUARE) {
            return _isPerfectSquare(number);
        }
        return false;
    }

    function _isPrime(uint256 n) internal pure returns (bool) {
        if (n < 2) return false;
        if (n == 2) return true;
        if (n % 2 == 0) return false;

        for (uint256 i = 3; i * i <= n; i += 2) {
            if (n % i == 0) return false;
        }
        return true;
    }

    function _isPiRelated(uint256 n) internal pure returns (bool) {
        // 314를 포함하거나 314의 배수
        string memory numStr = _toString(n);
        return _contains(numStr, "314") || n % 314 == 0;
    }

    function _isPerfectSquare(uint256 n) internal pure returns (bool) {
        if (n == 0) return true;
        uint256 sqrt = _sqrt(n);
        return sqrt * sqrt == n;
    }

    function _sqrt(uint256 x) internal pure returns (uint256) {
        if (x == 0) return 0;
        uint256 result = 1;
        if (x >> 128 > 0) {
            x >>= 128;
            result <<= 64;
        }
        if (x >> 64 > 0) {
            x >>= 64;
            result <<= 32;
        }
        if (x >> 32 > 0) {
            x >>= 32;
            result <<= 16;
        }
        if (x >> 16 > 0) {
            x >>= 16;
            result <<= 8;
        }
        if (x >> 8 > 0) {
            x >>= 8;
            result <<= 4;
        }
        if (x >> 4 > 0) {
            x >>= 4;
            result <<= 2;
        }
        if (x >> 2 > 0) {
            result <<= 1;
        }
        return result;
    }

    function _toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) return "0";
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }

    function _contains(string memory str, string memory substr) internal pure returns (bool) {
        bytes memory strBytes = bytes(str);
        bytes memory substrBytes = bytes(substr);

        if (substrBytes.length > strBytes.length) return false;

        for (uint256 i = 0; i <= strBytes.length - substrBytes.length; i++) {
            bool found = true;
            for (uint256 j = 0; j < substrBytes.length; j++) {
                if (strBytes[i + j] != substrBytes[j]) {
                    found = false;
                    break;
                }
            }
            if (found) return true;
        }
        return false;
    }

    // 조회 함수들
    function getMiningStats(
        address player
    )
        external
        view
        returns (uint256 attempts, uint256 successes, uint256 currentTPS, bool isActive, uint256 remainingTime)
    {
        MiningSession memory session = activeSessions[player];
        uint256 elapsed = block.timestamp - session.startTime;

        return (
            session.totalAttempts,
            session.totalSuccesses,
            elapsed > 0 ? session.totalAttempts / elapsed : 0,
            session.isActive,
            session.isActive
                ? (
                    session.startTime + MAX_MINING_DURATION > block.timestamp
                        ? session.startTime + MAX_MINING_DURATION - block.timestamp
                        : 0
                )
                : 0
        );
    }

    function getMiningHistory(address player, uint256 limit) external view returns (MiningAttempt[] memory) {
        MiningAttempt[] memory history = miningHistory[player];
        if (limit == 0 || limit > history.length) {
            return history;
        }

        MiningAttempt[] memory limitedHistory = new MiningAttempt[](limit);
        uint256 startIndex = history.length - limit;
        for (uint256 i = 0; i < limit; i++) {
            limitedHistory[i] = history[startIndex + i];
        }
        return limitedHistory;
    }

    function getRecentSuccesses(uint256 count) external view returns (MiningAttempt[] memory) {
        // 최근 성공 사례들을 반환 (메타 게임용)
        // 간단한 구현으로 현재 플레이어의 성공만 반환
        MiningAttempt[] memory history = miningHistory[msg.sender];
        if (count == 0 || count > history.length) {
            return history;
        }

        MiningAttempt[] memory limitedHistory = new MiningAttempt[](count);
        uint256 startIndex = history.length - count;
        for (uint256 i = 0; i < count; i++) {
            limitedHistory[i] = history[startIndex + i];
        }
        return limitedHistory;
    }

    // Pyth Entropy 콜백 함수
    function entropyCallback(uint64 sequenceNumber, address provider, bytes32 randomNumber) internal override {
        require(msg.sender == address(entropy), "Only entropy contract");
        require(provider == entropyProvider, "Invalid provider");

        PendingRequest memory request = pendingRequests[sequenceNumber];
        require(request.player != address(0), "Invalid request");

        // 요청 삭제 (재진입 방지)
        delete pendingRequests[sequenceNumber];

        // 라운드 유효성 확인
        GameManagerSimple.Round memory currentRound = gameManager.getCurrentRound();
        if (request.roundId != currentRound.roundId) {
            return; // 라운드가 변경된 경우 무시
        }

        // 세션 유효성 확인
        MiningSession storage session = activeSessions[request.player];
        if (!session.isActive) {
            return; // 세션이 종료된 경우 무시
        }

        // 채굴 성공 여부 확인
        bool success = _checkMiningSuccess(uint256(randomNumber), request.nftId, currentRound);

        if (success) {
            session.totalSuccesses++;
            playerTotalSuccesses[request.player]++;

            // 성공 기록
            miningHistory[request.player].push(
                MiningAttempt({
                    player: request.player,
                    nftId: request.nftId,
                    roundId: request.roundId,
                    randomNumber: uint256(randomNumber),
                    success: true,
                    timestamp: block.timestamp
                })
            );

            // 보상 지급
            rewardManager.distributeBasicReward(request.player);

            emit MiningSuccess(request.player, request.nftId, uint256(randomNumber), request.roundId, block.timestamp);
        }
    }

    // Admin functions
    function setGameManager(address _gameManager) external onlyOwner {
        gameManager = GameManagerSimple(_gameManager);
    }

    function setMinerNFT(address _minerNFT) external onlyOwner {
        minerNFT = MinerNFT(_minerNFT);
    }

    function setRewardManager(address _rewardManager) external onlyOwner {
        rewardManager = RewardManager(_rewardManager);
    }

    function forceEndSession(address player) external onlyOwner {
        require(activeSessions[player].isActive, "No active session");
        _endMiningSession(player);
    }

    function setEntropy(address _entropy) external onlyOwner {
        entropy = IEntropy(_entropy);
    }

    function setEntropyProvider(address _entropyProvider) external onlyOwner {
        entropyProvider = _entropyProvider;
    }

    function getPendingRequest(uint64 sequenceNumber) external view returns (PendingRequest memory) {
        return pendingRequests[sequenceNumber];
    }

    function fundForEntropy() external payable onlyOwner {
        // 컨트랙트에 ETH 추가 (entropy fee 지불용)
    }

    function withdrawETH(address to, uint256 amount) external onlyOwner {
        require(address(this).balance >= amount, "Insufficient balance");
        payable(to).transfer(amount);
    }

    // ETH 받기 위한 함수
    receive() external payable {}

    // IEntropyConsumer 인터페이스 구현
    function getEntropy() internal view override returns (address) {
        return address(entropy);
    }
}

contracts/RewardManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract RewardManager is Ownable, ReentrancyGuard {
    IERC20 public monToken;

    uint256 public constant BASIC_REWARD = 30 ether; // 30 MON per success
    uint256 public constant COMPLETION_BONUS = 500 ether; // 500 MON for completion
    uint256 public constant DEV_FEE_PERCENT = 10; // 10% dev fee

    address public devWallet;
    address public miningEngine;

    struct RewardStats {
        uint256 totalBasicRewards;
        uint256 totalCompletionBonuses;
        uint256 totalDevFees;
        uint256 totalDistributed;
    }

    mapping(address => uint256) public playerTotalEarned;
    mapping(address => uint256) public playerBasicRewards;
    mapping(address => uint256) public playerCompletionBonuses;

    RewardStats public rewardStats;

    event BasicRewardDistributed(address indexed player, uint256 amount, uint256 devFee, uint256 timestamp);

    event CompletionBonusDistributed(address indexed player, uint256 amount, uint256 timestamp);

    event DevFeeCollected(address indexed devWallet, uint256 amount, uint256 timestamp);

    modifier onlyMiningEngine() {
        require(msg.sender == miningEngine, "Only mining engine");
        _;
    }

    constructor(address _monToken, address _devWallet) Ownable(msg.sender) {
        monToken = IERC20(_monToken);
        devWallet = _devWallet;
    }

    function distributeBasicReward(address player) external onlyMiningEngine nonReentrant {
        require(player != address(0), "Invalid player address");

        uint256 devFee = (BASIC_REWARD * DEV_FEE_PERCENT) / 100;
        uint256 playerReward = BASIC_REWARD - devFee;

        // 플레이어에게 보상 지급
        require(monToken.transfer(player, playerReward), "Player reward transfer failed");

        // 개발팀에게 수수료 지급
        require(monToken.transfer(devWallet, devFee), "Dev fee transfer failed");

        // 통계 업데이트
        playerTotalEarned[player] += playerReward;
        playerBasicRewards[player] += playerReward;

        rewardStats.totalBasicRewards += playerReward;
        rewardStats.totalDevFees += devFee;
        rewardStats.totalDistributed += BASIC_REWARD;

        emit BasicRewardDistributed(player, playerReward, devFee, block.timestamp);
        emit DevFeeCollected(devWallet, devFee, block.timestamp);
    }

    function distributeCompletionBonus(address player) external onlyMiningEngine nonReentrant {
        require(player != address(0), "Invalid player address");

        // 완주 보너스는 개발팀 수수료 없이 전액 지급
        require(monToken.transfer(player, COMPLETION_BONUS), "Completion bonus transfer failed");

        // 통계 업데이트
        playerTotalEarned[player] += COMPLETION_BONUS;
        playerCompletionBonuses[player] += COMPLETION_BONUS;

        rewardStats.totalCompletionBonuses += COMPLETION_BONUS;
        rewardStats.totalDistributed += COMPLETION_BONUS;

        emit CompletionBonusDistributed(player, COMPLETION_BONUS, block.timestamp);
    }

    function batchDistributeRewards(address[] calldata players, uint256[] calldata amounts) external onlyOwner {
        require(players.length == amounts.length, "Arrays length mismatch");

        for (uint256 i = 0; i < players.length; i++) {
            require(monToken.transfer(players[i], amounts[i]), "Batch transfer failed");
            playerTotalEarned[players[i]] += amounts[i];
            rewardStats.totalDistributed += amounts[i];
        }
    }

    function emergencyWithdraw(address to, uint256 amount) external onlyOwner {
        require(monToken.transfer(to, amount), "Emergency withdrawal failed");
    }

    function getPlayerStats(
        address player
    ) external view returns (uint256 totalEarned, uint256 basicRewards, uint256 completionBonuses) {
        return (playerTotalEarned[player], playerBasicRewards[player], playerCompletionBonuses[player]);
    }

    function getContractBalance() external view returns (uint256) {
        return monToken.balanceOf(address(this));
    }

    function simulateROI(
        uint256 investmentCost,
        uint256 expectedSuccesses,
        bool willComplete
    ) external pure returns (uint256 totalRevenue, uint256 netProfit, uint256 roiPercent) {
        uint256 completionBonusTotal = willComplete ? COMPLETION_BONUS : 0;

        totalRevenue =
            (expectedSuccesses * (BASIC_REWARD - (BASIC_REWARD * DEV_FEE_PERCENT) / 100)) +
            completionBonusTotal;

        if (totalRevenue >= investmentCost) {
            netProfit = totalRevenue - investmentCost;
            roiPercent = (netProfit * 100) / investmentCost;
        } else {
            netProfit = 0;
            roiPercent = 0;
        }

        return (totalRevenue, netProfit, roiPercent);
    }

    function calculateRevenueProjection(
        uint256 successCount,
        bool completedMining
    ) external pure returns (uint256 totalPlayerReward, uint256 totalDevFee, uint256 totalCost) {
        uint256 basicRewardTotal = successCount * BASIC_REWARD;
        uint256 completionBonusTotal = completedMining ? COMPLETION_BONUS : 0;

        totalPlayerReward =
            (successCount * (BASIC_REWARD - (BASIC_REWARD * DEV_FEE_PERCENT) / 100)) +
            completionBonusTotal;
        totalDevFee = (successCount * (BASIC_REWARD * DEV_FEE_PERCENT)) / 100;
        totalCost = basicRewardTotal + completionBonusTotal;

        return (totalPlayerReward, totalDevFee, totalCost);
    }

    // Admin functions
    function setMonToken(address _monToken) external onlyOwner {
        monToken = IERC20(_monToken);
    }

    function setDevWallet(address _devWallet) external onlyOwner {
        devWallet = _devWallet;
    }

    function setMiningEngine(address _miningEngine) external onlyOwner {
        miningEngine = _miningEngine;
    }

    function fundContract(uint256 amount) external onlyOwner {
        require(monToken.transferFrom(msg.sender, address(this), amount), "Funding failed");
    }

    function getDetailedStats()
        external
        view
        returns (RewardStats memory stats, uint256 contractBalance, uint256 totalPlayers)
    {
        return (
            rewardStats,
            monToken.balanceOf(address(this)),
            0 // totalPlayers는 별도 카운터 필요
        );
    }
}
